import './App.css';
import './styles/indexStyle.css';
import './styles/paraCarrito.css';

import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import LoginForm from './Formularios/login.php'; // Ajusta la importación según tu estructura de carpetas

import React, { useEffect, useState } from 'react';

function App() {
  // Obtener el nombre de usuario de la URL
  //const params = new URLSearchParams(window.location.search);
  //const nameUser = params.get('name');

  //Obtener nombre de usuario de la URL con Router
   const [nameUser, setNameUser] = useState(/* Aquí tu lógica para obtener el nombre de usuario */);
    const [productos, setProductos] = useState([]);



    useEffect(() => {
      fetch('./Database/productos.json')
        .then(response => response.json())
        .then(data => setProductos(data))
        .catch(error => console.error('Error al cargar productos:', error));
    }, []);;

    const renderProductos = () => {
      const catalogoContainer = [];

      let seccionActual = null;
      let seccionActualElement = null;
      let nuevosHijos = [];

      productos.forEach(producto => {
        const { nombre, descripcion, section, precio, imagen } = producto;

        if (seccionActual !== section) {
          if (seccionActualElement !== null) {
            // Clonamos el elemento actual y le asignamos los nuevos hijos
            const nuevaSeccion = React.cloneElement(seccionActualElement, null, [...nuevosHijos]);
            catalogoContainer.push(nuevaSeccion);
          }

          const nuevoTitulo = (
            <h2 key={`titulo-${section}`} id={section.toLowerCase()} className="contenedor-items">
              {section}
            </h2>
          );

          catalogoContainer.push(nuevoTitulo);
          // Inicializamos los hijos de la sección actual como un array vacío
          nuevosHijos = [];

          seccionActualElement = (
            <section key={`seccion-${section}`} className={`wrap ${section}`}>
              {/* Aquí puedes agregar cualquier otro contenido para la sección */}
            </section>
          );


          seccionActual = section;
        }

        const nuevoProducto = (
          <article key={`producto-${nombre}`} className="tarjeta tarjetaAnimada item">
            <section className="face front">
              <img src={`./Images/${section}/${imagen}`} alt={nombre} className="img-item" />
              <section className="wrap-text_tarjeta">
                <article className="precio_tarjeta">
                  <span className="precio-item">{`${precio}$`}</span>
                </article>
                <span className="titulo-item">{nombre}</span>
              </section>
            </section>
            <section className="face back">
              <section className="wrap-text_tarjeta">
                <span className="titulo-item">{nombre}</span>
                <p>{descripcion}</p>
                <section className="cta-wrap_tarjeta">
                  <article className="precio_tarjeta">
                    <span className="precio-item">{`${precio}$`}</span>
                  </article>
                  <article className="cta_tarjeta">
                    <button className="boton-item" onClick={openAside}>
                      Agregar al Carrito
                    </button>
                  </article>
                </section>
              </section>
            </section>
          </article>
        );

        nuevosHijos.push(nuevoProducto);
      });


      if (seccionActualElement !== null) {
        const nuevaSeccion = React.cloneElement(seccionActualElement, null, [...nuevosHijos]);
        catalogoContainer.push(nuevaSeccion);
      }
      return catalogoContainer;
    };


    return (
      <div className="App">
        <header>
          <table>
            <tr>
              <td style={{ width: '5%' }}>
                <img
                  className="logoAnimado"
                  src="./Images/Iconos/Logotipo.png"
                  alt="Logotipo"
                  style={{ width: '250%', backgroundColor: '#ffff', borderRadius: '80px' }}
                />
              </td>
              <td style={{ width: '29%' }}>
                <a href="./Inicio/inicioL.html" className="text">
                  Inicio
                </a>
              </td>
              <td style={{ textAlign: 'center', width: '30%' }}>
                <h1>Banana's Cocktails</h1>
                <p>
                  <em>¡Cócteles exclusivos a un clic de distancia!</em>
                </p>
              </td>
              <td style={{ width: '20%' }}>
                <Router>
                  <Route path="/login">
                    {nameUser ? (
                      <Redirect to="/" />
                    ) : (
                      <LoginForm onLoginSuccess={(username) => setNameUser(username)} />
                    )}
                  </Route>
                </Router>
              </td>
              <td id="carritoContainer" style={{ width: '6%' }}>
                <img
                  className="carritoAnimado"
                  src="./Images/Iconos/CarritoCompra.png"
