import logo from './logo.svg';
import './App.css';

import React from 'react';

function App() {
  // Obtener el nombre de usuario de la URL
  const params = new URLSearchParams(window.location.search);
  const nameUser = params.get('name');

  return (
    <div className="App">
      <header>
        <table>
          <tr>
            <td style={{ width: '5%' }}>
              <img
                className="logoAnimado"
                src="./Images/Iconos/Logotipo.png"
                alt="Logotipo"
                style={{ width: '250%', backgroundColor: '#ffff', borderRadius: '80px' }}
              />
            </td>
            <td style={{ width: '29%' }}>
              <a href="./Inicio/inicioL.html" className="text">
                Inicio
              </a>
            </td>
            <td style={{ textAlign: 'center', width: '30%' }}>
              <h1>Banana's Cocktails</h1>
              <p>
                <em>¡Cócteles exclusivos a un clic de distancia!</em>
              </p>
            </td>
            <td style={{ width: '20%' }}>
              {nameUser ? (
                <h3 className="helloUser">Hola {nameUser}</h3>
              ) : (
                <a href="./Formularios/login.php" className="text">
                  Iniciar sesión
                </a>
              )}
            </td>
            <td id="carritoContainer" style={{ width: '6%' }}>
              <img
                className="carritoAnimado"
                src="./Images/Iconos/CarritoCompra.png"
                alt="Imagen de Carrito de Compras"
                style={{ width: '120%' }}
                id="carritoImagen"
                onClick={openAside}
              />
            </td>
          </tr>
        </table>
      </header>
    </div>
  );
}

export default App;
