import './App.css';

import React, { useState, useEffect } from 'react';

function App() {
  // Obtener el nombre de usuario de la URL
  const params = new URLSearchParams(window.location.search);
  const nameUser = params.get('name');


  return (
    <div className="App">
      <header>
        <table>
          <tr>
            <td style={{ width: '5%' }}>
              <img
                className="logoAnimado"
                src="./Images/Iconos/Logotipo.png"
                alt="Logotipo"
                style={{ width: '250%', backgroundColor: '#ffff', borderRadius: '80px' }}
              />
            </td>
            <td style={{ width: '29%' }}>
              <a href="./Inicio/inicioL.html" className="text">
                Inicio
              </a>
            </td>
            <td style={{ textAlign: 'center', width: '30%' }}>
              <h1>Banana's Cocktails</h1>
              <p>
                <em>¡Cócteles exclusivos a un clic de distancia!</em>
              </p>
            </td>
            <td style={{ width: '20%' }}>
              {nameUser ? (
                <h3 className="helloUser">Hola {nameUser}</h3>
              ) : (
                <a href="./Formularios/login.php" className="text">
                  Iniciar sesión
                </a>
              )}
            </td>
            <td id="carritoContainer" style={{ width: '6%' }}>
              <img
                className="carritoAnimado"
                src="./Images/Iconos/CarritoCompra.png"
                alt="Imagen de Carrito de Compras"
                style={{ width: '120%' }}
                id="carritoImagen"
                onClick={openAside}
              />
            </td>
          </tr>
        </table>
      </header>

      <nav class="navul">
        <ul>
          <strong>
            <li class="navli"><a href="#whisky" class="nava">Whisky</a></li>
            <li class="navli"><a href="#ron" class="nava">Ron</a></li>
            <li class="navli"><a href="#tequila" class="nava">Tequila</a></li>
            <li class="navli"><a href="#vodka" class="nava">Vodka</a></li>
            <li class="navli"><a href="#gin" class="nava">Gin</a></li>
            <li class="navli"><a href="#vino" class="nava">Vino</a></li>
          </strong>
        </ul>
      </nav>

      {/*<!-- Mostrar productos seleccionados --> */}
      <aside id="popup">

        <button class="closebtn" onclick="closeAside()">Cerrar</button>
        <div class="aside-content">
          {/* <!-- Carrito de Compras --> */}
          <div class="carrito" id="carrito">
            <div class="header-carrito">
              <h3>Tu Carrito</h3>
            </div>

            <div class="carrito-items">
             {/*  <!--Aquí se mostrarán los productos seleccionados--> */}
            </div>
            <div class="carrito-total">
              <div class="fila">
                <strong>Tu Total</strong>
                <span class="carrito-precio-total">
                  $120.000,00
                </span>
              </div>
              <button class="btn-pagar">Pagar <i class="fa-solid fa-bag-shopping"></i> </button>
            </div>
          </div>
        </div>

      </aside>

      {renderTarjetas()}

    </div>
  );
}




function openAside() {
  var popup = document.getElementById("popup");
  var carritoImagen = document.getElementById("carritoImagen");

  popup.style.width = "30%";

  carritoImagen.style.display = "none";
}

function closeAside() {
  var popup = document.getElementById("popup");
  var carritoImagen = document.getElementById("carritoImagen");

  popup.style.width = "0%";

  carritoImagen.style.display = "block";
}


export default App;
