import React, { useState } from 'react';
import '../styles/loginStyle.css'; // Asegúrate de importar tu archivo de estilos

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [remember, setRemember] = useState(false);

    const handleSubmit = (event) => {
        event.preventDefault();
        // Aquí puedes agregar la lógica para manejar el envío del formulario
        // Por ejemplo, puedes llamar a una función que procese la autenticación
    };

    return (
        <section>
            <article className="contenedor">
                <article className="formulario">
                    <form onSubmit={handleSubmit}>
                        {/* Lógica de PHP que tenías dentro del formulario <?php
          include('controlador.php');
          ?> */}
                        <center>
                            <img
                                className="logoAnimado"
                                src="../Images/Iconos/Logotipo.png"
                                alt="Logotipo"
                                style={{ width: '35%', backgroundColor: '#ffff', borderRadius: '80px' }}
                            />
                        </center>

                        <h2>Inicio de sesión</h2>

                        <article className="inputContenedor">
                            <i className="fas fa-envelope"></i>
                            <input
                                type="email"
                                name="email"
                                id="email"
                                maxLength="100"
                                onChange={(e) => setEmail(e.target.value)}
                                value={email}
                                required
                            />
                            <label htmlFor="email">Email</label>
                        </article>
                        <article className="inputContenedor">
                            <i className="fas fa-lock"></i>
                            <input
                                type="password"
                                name="password"
                                id="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />
                            <label htmlFor="password">Contraseña</label>
                        </article>

                        <article className="olvidar">
                            <label>
                                <input
                                    type="checkbox"
                                    name="recordar"
                                    id="recordar"
                                    checked={remember}
                                    onChange={() => setRemember(!remember)}
                                />
                                <span className="recordar">Recordarme</span>

                                <a href="#">Olvide mi contraseña</a>
                            </label>
                        </article>

                        <div className="sectionAccess">
                            <input type="submit" value="Acceder" />
                            <article className="registrar">
                                <p>
                                    ¿No tienes cuenta? <a href="./register.php">Registrate</a>
                                </p>
                            </article>
                        </div>
                    </form>
                </article>
            </article>
        </section>
    );
};

export default Login;
