import './App.css';

import React, { useState, useEffect } from 'react';

function App() {
  // Obtener el nombre de usuario de la URL
  const params = new URLSearchParams(window.location.search);
  const nameUser = params.get('name');
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Cargar productos desde el archivo JSON (usando una ruta relativa)
    fetch('./Database/productos.json')
      .then(response => response.json())
      .then(data => setProductos(data))
      .catch(error => console.error('Error al cargar productos:', error));
  }, []);

  const renderProductos = () => {
    const catalogoContainer = [];

    let seccionActual = null;
    let seccionActualElement = null;

    productos.forEach(producto => {
      const { nombre, descripcion, section, precio, imagen } = producto;

      if (seccionActual !== section) {
        if (seccionActualElement !== null) {
          catalogoContainer.push(seccionActualElement);
        }

        const nuevoTitulo = (
          <h2 key={`titulo-${section}`} id={section.toLowerCase()} className="contenedor-items">
            {section}
          </h2>
        );

        catalogoContainer.push(nuevoTitulo);

        seccionActualElement = (
          <section key={`seccion-${section}`} className={`wrap ${section}`}>
            {/* Aquí puedes agregar cualquier otro contenido para la sección */}
          </section>
        );

        seccionActual = section;
      }

      const nuevoProducto = (
        <article key={`producto-${nombre}`} className="tarjeta tarjetaAnimada item">
          {/* Contenido del producto */}
        </article>
      );

      seccionActualElement.props.children.push(nuevoProducto);
    });

    if (seccionActualElement !== null) {
      catalogoContainer.push(seccionActualElement);
    }

    return catalogoContainer;
  };


  return (
    <div className="App">
      <header>
        <table>
          <tr>
            <td style={{ width: '5%' }}>
              <img
                className="logoAnimado"
                src="./Images/Iconos/Logotipo.png"
                alt="Logotipo"
                style={{ width: '250%', backgroundColor: '#ffff', borderRadius: '80px' }}
              />
            </td>
            <td style={{ width: '29%' }}>
              <a href="./Inicio/inicioL.html" className="text">
                Inicio
              </a>
            </td>
            <td style={{ textAlign: 'center', width: '30%' }}>
              <h1>Banana's Cocktails</h1>
              <p>
                <em>¡Cócteles exclusivos a un clic de distancia!</em>
              </p>
            </td>
            <td style={{ width: '20%' }}>
              {nameUser ? (
                <h3 className="helloUser">Hola {nameUser}</h3>
              ) : (
                <a href="./Formularios/login.php" className="text">
                  Iniciar sesión
                </a>
              )}
            </td>
            <td id="carritoContainer" style={{ width: '6%' }}>
              <img
                className="carritoAnimado"
                src="./Images/Iconos/CarritoCompra.png"
                alt="Imagen de Carrito de Compras"
                style={{ width: '120%' }}
                id="carritoImagen"
                onClick={openAside}
              />
            </td>
          </tr>
        </table>
      </header>

      <nav class="navul">
        <ul>
          <strong>
            <li class="navli"><a href="#whisky" class="nava">Whisky</a></li>
            <li class="navli"><a href="#ron" class="nava">Ron</a></li>
            <li class="navli"><a href="#tequila" class="nava">Tequila</a></li>
            <li class="navli"><a href="#vodka" class="nava">Vodka</a></li>
            <li class="navli"><a href="#gin" class="nava">Gin</a></li>
            <li class="navli"><a href="#vino" class="nava">Vino</a></li>
          </strong>
        </ul>
      </nav>

      {/*<!-- Mostrar productos seleccionados --> */}
      <aside id="popup">

        <button class="closebtn" onclick="closeAside()">Cerrar</button>
        <div class="aside-content">
          {/* <!-- Carrito de Compras --> */}
          <div class="carrito" id="carrito">
            <div class="header-carrito">
              <h3>Tu Carrito</h3>
            </div>

            <div class="carrito-items">
             {/*  <!--Aquí se mostrarán los productos seleccionados--> */}
            </div>
            <div class="carrito-total">
              <div class="fila">
                <strong>Tu Total</strong>
                <span class="carrito-precio-total">
                  $120.000,00
                </span>
              </div>
              <button class="btn-pagar">Pagar <i class="fa-solid fa-bag-shopping"></i> </button>
            </div>
          </div>
        </div>

      </aside>

      <section className="catalogo" id="catalogo-container">
        {renderProductos()}
      </section>

    </div>
  );
}




function openAside() {
  var popup = document.getElementById("popup");
  var carritoImagen = document.getElementById("carritoImagen");

  popup.style.width = "30%";

  carritoImagen.style.display = "none";
}

function closeAside() {
  var popup = document.getElementById("popup");
  var carritoImagen = document.getElementById("carritoImagen");

  popup.style.width = "0%";

  carritoImagen.style.display = "block";
}


export default App;
