//import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Inicio from "./components/InicioL";
import Tienda from "./components/Tienda";
import Login from "./components/Login";
import Factura from "./components/Factura";

import AdminHome from './components/cliente/AdminHome';
import { Redirect } from 'react-router-dom';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  return (

    <div className="App">

      <Router>
        <Routes>
          <Route path="/" element={<Inicio />} />
          <Route path="/inicio" element={<Inicio />} />
          <Route path="/tienda" element={<Tienda />} />
          <Route path="/factura" element={<Factura />} />
          <Route path="/login" element={<Login />} />
          <Route
            path="/login"
            render={(props) => (
              <Login {...props} setEncontrado={setLoggedIn} setIsAdmin={setIsAdmin} />
            )}
          />
          {/* SOlo para Adiministrador */}
          <Route
            path="/adminHome"
            render={(props) => {
              console.log('Renderizando AdminHome...');
              return isLoggedIn && isAdmin ? (
                <AdminHome {...props} />
              ) : (
                <Navigate to="/login" />
              );
            }}
          />


        </Routes>
      </Router>

    </div>
  );
}



export default App;
