import React from 'react'
import axios from "axios";
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { validarSoloLetras, validarCampo, validarSoloNumeros, validarCedulaInput } from '../../scripts/validacion';
import '../../styles/registerStyle.css';

const URI = 'http://localhost:8000/cliente/';

const CreateCliente = () => {

    const [nombre, setNombre] = useState('');
    const [apellido, setApellido] = useState('');
    const [fechaNacimiento, setFechaNacimiento] = useState('');
    const [celular, setCelular] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [esAdmin, setEsAdmin] = useState(false); //por defecto el usuario no es admin
    const [domicilio, setDomicilio] = useState('');
    const [cedula, setCedula] = useState('');
    const navigate = useNavigate();

    const store = async (e) => {
        e.preventDefault(); //para evitar el submit por defecto del form
        await axios.post(URI, { NOMBREU: nombre, APELLIDOU: apellido, FECHANACIMIENTOU: fechaNacimiento, CELULARU: celular, EMAILU: email, PASSWORDU: password, ESADMIN: esAdmin, DOMICILIOCLI: domicilio, CEDULACLI: cedula });
        navigate('/gestionClientes');
    }

    return (
        <div className="body-Register">
            <section className='section-Register'>
                <article className="contenedor-Register">
                    <center>
                        <img
                            className="logoAnimado"
                            src="../Images/Iconos/Logotipo.png"
                            alt="Logotipo"
                            style={{ width: '35%', backgroundColor: '#ffff', borderRadius: '80px' }}
                        />
                    </center>
                    <article className="formulario">
                        <h2>Registrate</h2>
                        <form onSubmit={store}>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-user"></i>
                                <input
                                    type="text"
                                    name="nombre"
                                    id="registerName"
                                    pattern="[A-Za-z]+"
                                    maxLength="100"
                                    onKeyPress={(event) => validarSoloLetras(event)}
                                    onInput={(e) => validarCampo(e.target)}
                                    onChange={(e) => setNombre(e.target.value)}
                                    required
                                />
                                <label htmlFor="nombreRegister" className='form-label'>Nombre</label>
                            </article>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-user"></i>
                                <input
                                    type="text"
                                    name="apellido"
                                    id="registerSurname"
                                    pattern="[A-Za-z]+"
                                    maxLength="100"
                                    onKeyPress={(event) => validarSoloLetras(event)}
                                    onInput={(e) => validarCampo(e.target)}
                                    onChange={(e) => setApellido(e.target.value)}
                                    required
                                />
                                <label htmlFor="apellido">Apellido</label>
                            </article>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-calendar-alt"></i>
                                <input type="date" name="dateOfBirth" id="dateOfBirth"
                                    onChange={(e) => setFechaNacimiento(e.target.value)}
                                    required />
                                <label htmlFor="dateOfBirth">Fecha de nacimiento</label>
                            </article>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-phone"></i>
                                <input
                                    type="number"
                                    name="celular"
                                    id="celular"
                                    pattern="[0-9]+"
                                    maxLength="10"
                                    onKeyPress={(event) => validarSoloLetras(event)}
                                    onInput={(e) => validarCampo(e.target)}
                                    onChange={(e) => setCelular(e.target.value)}
                                    required
                                />
                                <label htmlFor="celular">Celular</label>
                            </article>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-home"></i>
                                <textarea cols="35" rows="7" id="direccion" name="domicilio" pattern="[A-Za-z0-9\s\-\.,]+"
                                    className='textArea-Register'
                                    onInput={(e) => validarCampo(e.target)}
                                    onChange={(e) => setDomicilio(e.target.value)}
                                    required ></textarea>

                                <label htmlFor="domicilio">Domicilio</label>
                            </article>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-id-card"></i>
                                <input id="cedula" name="cedula" placeholder="Ingresa tu número de cedula" minlength="10"
                                    pattern="[0-9]{10}" onkeypress={(e) => validarSoloNumeros(e.event)}
                                    oninput={(e) => validarCedulaInput(e.target)}
                                    onChange={(e) => setCedula(e.target.value)}
                                    required />
                                <p id="mensajeError" style={{ color: 'red' }}></p>

                                <label htmlFor="cedula">Cedula</label>
                            </article>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-envelope"></i>
                                <input
                                    type="email"
                                    name="email"
                                    id="emailRegister"
                                    onInput={(e) => validarCampo(e.target)}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                                <label htmlFor="usuario">Email</label>
                            </article>
                            <article className="inputContenedor-Register">
                                <i className="fas fa-lock"></i>
                                <input type="password" name="password" id="passwordRegister"
                                    onInput={(e) => validarCampo(e.target)}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required />
                                <label htmlFor="password">Contraseña</label>
                            </article>

                            <article className="registrar">
                                <input name="buttonRegister" className="buttonRegister"
                                    type="submit" value="Registrarse" />
                            </article>
                        </form>
                    </article>
                </article>
            </section>
        </div>
    )
}

export default CreateCliente
