import React from 'react'
import axios from "axios";
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {validarSoloLetras,validarCampo} from '.../scripts/validacion';
import '.../styles/registerStyle.css';

const URI = 'http://localhost:8000/cliente/';

const CreateCliente = () => {

const[nombre,setNombre]=useState('');
const[apellido,setApellido]=useState('');
const[fechaNacimiento,setFechaNacimiento]=useState('');
const[celular,setCelular]=useState('');
const[email,setEmail]=useState('');
const[domicilio,setDomicilio]=useState('');
const[cedula,setCedula]=useState('');
const navigate = useNavigate();

const store = async (e) => {
    e.preventDefault(); //para evitar el submit por defecto del form
    await axios.post(URI, { NOMBREU: nombre, APELLIDOU: apellido, FECHANACIMIENTOU: fechaNacimiento, CELULARU: celular, EMAILU: email, DOMICILIOCLI: domicilio, CEDULACLI: cedula });
    navigate('/gestionClientes');
}

    return (
        <div>
            <h3>Crear Cliente</h3>
        </div>
    )
}

export default CreateCliente
