import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const URI = 'http://localhost:8000/cliente/';

const CompShowClientes = () => {
    const [cliente, setCliente] = useState([]);
    useEffect(() => {
        getClientes();
    }, []);


    //procedimiento para mostrar todos los clientes
    const getClientes = async () => {
        const res = await axios.get(URI);
        if (res && res.data) {
            setCliente(res.data);
        }
    }


    const deleteCliente = async (IDCLIENTE) => {
        axios.delete(`${URI}${IDCLIENTE}`);
        getClientes();
    }


    return (
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <table className='table'>
                        <thead className='table-primary'>
                            <tr>
                                <th>ID</th>
                                <th>Nombre</th>
                                <th>Apellido</th>
                                <th>Fecha de nacimiento</th>
                                <th>Celular</th>
                                <th>Correo</th>
                                <th>Domicilio</th>
                                <th>Cedula</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cliente.map((cliente) => (
                                <tr key={cliente.IDCLIENTE}>
                                    <td>{cliente.IDCLIENTE}</td>
                                    <td>{cliente.NOMBRE}</td>
                                    <td>{cliente.APELLIDO}</td>
                                    <td>{cliente.FECHANACIMIENTO}</td>
                                    <td>{cliente.CELULAR}</td>
                                    <td>{cliente.CORREO}</td>
                                    <td>{cliente.DOMICILIO}</td>
                                    <td>{cliente.CEDULA}</td>
                                    <td>
{/*                                         <Link to={`/edit/${cliente.IDCLIENTE}`} className='btn btn-info'>Editar</Link>
 */}                                        <button onClick={() => deleteCliente(cliente.IDCLIENTE)} className='btn btn-danger'>Eliminar</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

        </div>
    );
}

export default CompShowClientes;
